(function(e){abcficAxResize={settings:{url:abcfic_localize_vars.ajaxurl,type:"POST",action:abcfic_localize_vars.action,nonce:abcfic_localize_vars.nonce,ids:abcfic_localize_vars.ids,permission:abcfic_localize_vars.permission,error:abcfic_localize_vars.error,no_img_id:abcfic_localize_vars.no_img_id,failure:abcfic_localize_vars.failure,no_img:abcfic_localize_vars.no_img,no_operation:abcfic_localize_vars.no_operation,err_timeout:abcfic_localize_vars.err_timeout,timeout:abcfic_localize_vars.timeout,end_ok:""},run:function(t){sr=this.settings;e.ajax({type:"POST",url:sr.url,data:"action="+sr.action+"&operation="+sr.operation+"&_wpnonce="+sr.nonce+"&img_id="+sr.ids[t],cache:false,timeout:sr.timeout,success:function(e){console.log("Ajax-resize image processed:");console.log(sr.ids[t]);console.log("Ajax-resize status:");console.log(e);switch(e){case"OK":break;case"no_img":abcficPBar.addNote(sr.no_img);break;case"-1":abcficPBar.addNote(sr.permission);break;case"no_img_id":abcficPBar.addNote(sr.no_img_id);break;case"error":abcficPBar.addNote(sr.error);break;case"no_operation":abcficPBar.addNote(sr.no_operation);break;default:abcficPBar.addNote("Img "+sr.ids[t]+": "+sr.failure,e);break}},error:function(e,n,r){console.log("Ajax-resize error");console.log(n);if(n==="timeout"){abcficPBar.addNote("Img "+sr.ids[t]+": "+sr.err_timeout)}else{abcficPBar.addNote("Img "+sr.ids[t]+": "+sr.failure,n)}},complete:function(){t++;abcficPBar.increase(t);if(t<sr.ids.length){abcficAxResize.run(t)}else{abcficPBar.finished();jQuery("#uploadStatusCntr").append(sr.end_ok)}}})},init:function(t){var n=0;this.settings=e.extend({},this.settings,{},t||{});console.log("Ajax-resize images to process:");console.log(this.settings.ids);this.run(n)}}})(jQuery)